', onSuccess: function(responseText){$(\'jformparamsteam_id\').innerHTML = responseText;}}).send();"';		$javascript = 'onchange="javascript:  var myRequest = new Ajax(\'index3.php?option=com_joomsport&task=getparcip&tmpl=component&sid=\'+$(\'playerzdf_id\').value, { method: \'post\', update: $(\'paramsteam_id\')}).request();"';		$jqre = '<select name="params[sidgid]" id="playerzdf_id" class="inputbox" size="1" '.$javascript.'>';		$jqre .= '<option value="0">'.$lang_val2.'</option>';		$selectbox = array();		for($i=0;$i<count($rows);$i++){			$row = $rows[$i];			$jqre .= '<optgroup label="'.$row->name.'">';			$query = "SELECT group_name,id FROM #__bl_groups  WHERE s_id = ".$row->id;			$db->setQuery($query);			$gr = $db->loadObjectList();			$jqre .= '<option value="'.$row->id.'|0" '.(($value==($row->id.'|0'))?"selected":"").'>'.$lang_val.'</option>';			for($j=0;$j<count($gr);$j++){				//$selectbox[] = JHTML::_('select.option',  $row->id.'|'.$gr[$j]->id,$gr[$j]->group_name, 'id', 'name' ); 				$jqre .= '<option value="'.$row->id.'|'.$gr[$j]->id.'" '.(($value==($row->id.'|'.$gr[$j]->id))?"selected":"").'>'.$gr[$j]->group_name.'</option>';			}			$jqre .= '</optgroup>';		}		$jqre .= '</select>';								$html = JHTML::_('select.genericlist',   $rows, 'params[sidgid]', 'class="inputbox" size="1"', 'id', 'name', $value);		//$html .= "\n".'<input type="hidden" id="'.$name.'_id" name="'.$fieldName.'" value="'.$value.'" />';				return $jqre;	}}